<?phpclass addKeyView extends AjaxView {	protected $module = 'project';	protected function _prepare() {		$this->setModule();		global $Config;		$project = new Project( $_POST['id'] );		$res = $project->addKey( $_POST['key'] );		$out = array();		if ( $res === true ) {			$out['error'] = false;			$out['run'] = array( 'location.reload()' );		} else {			$out['error'] = $res;		}				$Config->Templ->Add( 'inline', json_encode( $out ) );    }};