<?phpclass deleteView extends AjaxView {	protected $module = 'project';	protected function _prepare() {		$this->setModule();		global $Config;		$project = new Project( null, $_GET['id'] );		if ( $_GET['confirm'] == '1' ) {			if ( !$project->isEditable() ) {        		Misc::Load( '/404' );        		return;        	}			$project->delete();  			Misc::Load( '/' );			return;		}				$Config->Templ->Add( 'delete', $project->getDetails() );    }};