<?phpclass contactView extends AjaxView {	protected $module = 'user';	protected function _prepare() {		$this->setModule();			global $User;		global $Config;		$out = [ 'error' => false ];		if ( !empty( $_POST['email'] ) && !empty( $_POST['name'] ) && !empty( $_POST['message'] ) ) {			mail( 'admin@phpfy.net ', 'Email from PHPfy!', $_POST['email'] . "\n" . $_POST['name'] . "\n\n" . $_POST['message'] );		} else {			$out['error'] = [ 'Invalid form data' ];		}		$Config->Templ->Add( 'inline', json_encode( $out ) );    }};