<?phpclass linkedinView extends View {	protected $module = 'user';	protected function _prepare() {		$this->setModule();			global $User;		global $Config;		$conf = $Config->getConfig( 'social' );		try {			$linkedin = new LinkedinApi( $conf['linkedin']['key'], $conf['linkedin']['secret'] );			$User->socialLogin( $linkedin->getUserDetails() );						$unlock = '';			if ( !empty( $_SESSION['login_from'] ) ) {				$project = new Project( null, $_SESSION['login_from'] );				if ( $project->isEditable() ) $unlock = 'true';			}			$Config->Templ->AddScript( 'inline_js', 'opener.phpfy.social.success(' . $unlock . '); opener.focus(); window.close();' );		} catch( Exception $e ) {			$Config->Templ->AddScript( 'inline_js', 'opener.phpfy.social.error("' . str_replace( ['"', "\n","\r"], "'", $e->getMessage() ) . '"); opener.focus(); window.close();' );		}    }};