<?phpclass userView extends AjaxView {	protected $module = 'user';	protected function _prepare() {		$this->setModule();			global $User;		global $Config;		if ( !$User->isLog() ) {			$Config->Templ->Add( 'login-register', array( 'project_id' => $_GET['id'] ) );			$Config->Templ->AddScript( 'inline_file', 'social.js' );			return;		} 		$data = $User->getDetails();		$data['open'] = 'info';		$Config->Templ->Add( 'userdetails', $data );    }};